              >
                {services.map((service, i) => {
                  const angle = (360 / services.length) * i - 90
                  const rad = (angle * Math.PI) / 180
                  const x = center + radius * Math.cos(rad) - cardSize / 2
                  const y = center + radius * Math.sin(rad) - cardSize / 2

                  const CardInner = (
                    <>
                      {/* Gradient overlay */}
                      <div
