    setTimeout(() => {
      setIsLoading(false)
      setDeployStatus("Déployé avec succès")
    }, 1500)
  }

  // Auto-cycle through servers for demo effect
  useEffect(() => {
    const interval = setInterval(() => {
      if (!isLoading) {
        setActiveServer((prev) => {
